const Switch = () => {
    const score = React.useRef(null);
    console.log(this.Switch.current);
}

const SwitchEvent = () => {
    this.Switch.current.style.background = "yellow"
}

React.useEffect(() => {
    this.Switch.addEventListener("click", SwitchEvent)
})

const edit = (props) => {
    const day = '금'
    return (
        <div className='edit'>
            <h1>{day}요일 평점 남기기</h1>
            <div className='row'>
                <div className='score' ref={Switch}></div>
                <div className='score'></div>
                <div className='score'></div>
                <div className='score'></div>
                <div className='score'></div>
            </div>
            <Link to="/">
                <button onClick={submit}>평점 남기기</button>
            </Link>

        </div>
    )
}

export default edit;
