  const [friScore, setFriScore] = React.useState(arr.find( ({ name }) => name === '금' )['score']);
  const [satScore, setSatScore] = React.useState(arr.find( ({ name }) => name === '토' )['score']);
  const [sunScore, setSunScore] = React.useState(arr.find( ({ name }) => name === '일' )['score']);

  const random = Math.floor(Math.random() * 6)

  useEffect(() => {  
    setMonScore(this.monScore+this.random)
}, [])


  return (

    <div>
        {arr.map((value, index) => {
