import React, {useEffect} from "react";
import { Link } from "react-router-dom";
import Score from "./score";

const ScoreList = () => {
  const arr = [{day: '월', score: 0},{day: '화', score:0},{day: '수', score:0},{day: '목', score:0},{day: '금', score:0},{day: '토', score:0},{day: '일', score:0}]
  const [day, setDay] = React.useState(arr);
  const [score, setScore] = React.useState(0);
  console.log(day)

  useEffect(() => {
    setScore(Math.floor(Math.random() * 6))
}, [])

  return (
    <div>
        {day.map((day, index) => {
            return (
                <div className="row" key={index}>
                    <p>{day[index][day]}{score}</p>
                    <Score index={index} score={score} />
                    <Link to="/edit" name="{day}">
                      <div className="editBtn"></div>
                    </Link>
                </div>
            )
        })}
    </div>
  )

}


