import React, { useEffect } from "react";
import { Link } from "react-router-dom";

const submit = () => {

}




const Edit = (props) => {
    const day = '금'

    const score = React.useRef(null);
    console.log(score.current)

    const SwitchEvent = () => {

        score.current.style.background = "yellow"
    }

    useEffect(() => {
        score.current.addEventListener("click", SwitchEvent)
    })

    return (
        <div className='edit'>
            <h1>{day}요일 평점 남기기</h1>
            <div className='row'>
                <div className='score' ref={score}></div>
                <div className='score'></div>
                <div className='score'></div>
                <div className='score'></div>
                <div className='score'></div>
            </div>
            <Link to="/">
                <button onClick={submit}>평점 남기기</button>
            </Link>

        </div>
    )
}

export default Edit;
