import React, {useEffect} from "react";
import { Link } from "react-router-dom";
import Score from "./score";

const ScoreList = () => {


  const [day, setDay] = React.useState(['월','화','수','목','금','토','일']);
  const [score, setScore] = React.useState(0);

  useEffect(() => {
    const newArray = Math.floor(Math.random() * 6);
    console.log(newArray)
    setScore(newArray)
}, [])

  return (
    <div>
        {day.map((day, index) => {
            return (
                <div className="row" key={index}>
                    <p>{day}{score}</p>
                    <Score index={day} score={score} />
                    <Link to="/edit" name="{day}">
                      <div className="editBtn"></div>
                    </Link>
                </div>
            )
        })}
    </div>
  )

}


