const Score = (props) => {

    const [scores, setScore] = React.useState(0);

    const updateScore = () => {
        // const array = [0, 0, 0, 0, 0]
        // const newArray = array.map((array_item) => {
        //     return array_item + Math.floor(Math.random() * 6);
        // });
        setScore(scores + 1)
    }
    updateScore()

    return (
        <div className="scoreList">
            <div className="score active">{scores}</div>
        </div>
    )

}

export default Score;
